import React from 'react';

const Newsletter = () => {
  return (
    <div className='w-full py-16 text-white px-4'>
      <div className='max-w-[1240px] mx-auto grid lg:grid-cols-3'>
        <div className='lg:col-span-2 my-4'>
          <h1 className='md:text-4xl sm:text-3xl text-2xl'>Want tips & tricks to optimize your flow?</h1>
          <p>Sign up for our newsletter and stay up to date.</p>
        </div>
        <div className='my-4 mx-auto'>
          <div className='flex flex-col sm:flex-row items-center m-2 justify-between w-full'>
            <input className='p-3 flex w-full rounded-md text-black' type="email" name="" id="" placeholder='Enter Email' />
            <button className='bg-[#00df9a] w-[160px] rounded-md font-medium py-2 text-black mt-2 sm:mt-4 lg:mt-0'>
              Notify Me
            </button>
          </div>
          <p>We care about the protection of your data. Read our <span className='text-[#00df9a]'>Privacy Policy.</span></p>
        </div>
      </div>
    </div>
  );
};

export default Newsletter;
